import { Component } from '@angular/core';

@Component({
  selector: 'app-options',
  templateUrl: './options.component.html',
  styleUrls: ['./options.component.css']
})
export class OptionsComponent {
  title = 'Game Options'
  mineCount: number = 3;
  rowCount: number = 5;
  columnCount: number = 5;

  @ViewChild('placeholder', { read: ViewContainerRef, static: true })
  public placeholder!: ViewContainerRef;
  constructor(private resolver: ComponentFactoryResolver, private injector: Injector) { }

  mineInputChange(event: { target: { value: number; }; }) {
    this.mineCount = event.target.value;
  }

  rowInputChange(event: { target: { value: number; }; }) {
    this.rowCount = event.target.value;
  }

  columnInputChange(event: { target: { value: number; }; }) {
    this.columnCount = event.target.value;
  }
}
