import { Component, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-grid-squares',
  templateUrl: './grid-squares.component.html',
  styleUrls: ['./grid-squares.component.css']
})
export class GridSquaresComponent {
  @Input() id: number | undefined;
  @Input() bomb: boolean | undefined;
  @Input() coordinates: [number, number] | undefined;

  @Output() sendCoordinatesEvent: EventEmitter<[number, number]> = new EventEmitter<[number, number]>();
  adjacentBombs: number = 0;
  value: string = "Bomb?";

  ngOnInit() {

  }

  chooseSquare() {
    if (this.bomb) {
      this.value = "Bomb!";
    }
    else {
      this.value = `${this.adjacentBombs}`;
      this.sendCoordinatesEvent.emit(this.coordinates);
    }
  }


}
<button (click)="chooseSquare()">{{value}}</button>
