import { Component, ViewChild, ViewContainerRef, ComponentFactoryResolver, Injector, Input, EventEmitter, Output } from '@angular/core';
import { GameStatusComponent } from '../game-status/game-status.component';
import { GameOptionsData } from '../game-options-data';

@Component({
  selector: 'app-options',
  templateUrl: './options.component.html',
  styleUrls: ['./options.component.css']
})
export class OptionsComponent {
  title = 'Game Options'

  @Input() data: GameOptionsData = {
    mineCount: 1,
    columnCount: 1,
    rowCount: 1
  };
  @Output() optionsChangedEvent: EventEmitter<GameOptionsData> = new EventEmitter();

  mineInputChange(event: { target: { value: number; }; }) {
    if (event.target.value < 1) {
      event.target.value = 1;
    }

    this.data.mineCount = Number(event.target.value);
  }

  rowInputChange(event: { target: { value: number; }; }) {
    if (event.target.value < 1) {
      event.target.value = 1;
    }

    this.data.rowCount = Number(event.target.value);
  }

  columnInputChange(event: { target: { value: number; }; }) {
    if (event.target.value < 1) {
      event.target.value = 1;
    }

    this.data.columnCount = Number(event.target.value);
  }

  optionsChanged() {

    const data: GameOptionsData = {
      mineCount: this.data.mineCount,
      columnCount: this.data.columnCount,
      rowCount: this.data.rowCount
    }

    this.optionsChangedEvent.emit(data);
  }
}

<div id="gameOptionsForm">

  <div class="container">
    <div class="row">
      <div class="col-10 col-md col-xl-12">
        <div class="input-group">
          <label class="input-group-text" for="columnCount">Columns</label>
          <input #columnAmount class="form-control" id="columnCount" type="number" required value="{{data.columnCount}}" min="1" (change)="columnInputChange($any($event))" />
        </div>
      </div>
      <div class="col-10 col-md col-xl-12">
        <div class="input-group">
          <label class="input-group-text" for="rowCount">Rows</label>
          <input #rowAmount class="form-control" id="rowCount" type="number" required value="{{data.rowCount}}" min="1" (change)="rowInputChange($any($event))" />
        </div>
      </div>
      <div class="col-10 col-md col-xl-12">
        <div class="input-group w-100">
          <label class="input-group-text" for="mineCount">Mines</label>
          <input #mineAmount class="form-control" id="mineCount" type="number" required value="{{data.mineCount}}" min="1" (change)="mineInputChange($any($event))" />
        </div>
      </div>
    </div>
  </div>

</div>

<div id="applyOptionsDiv">
  <button id="applyOptions" class="btn btn-primary" (click)="optionsChanged()">Apply&nbsp;<i class="bi bi-check2"></i></button>
</div>
