@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Angular Minesweeper';
}
<style></style>
<header></header>
<body>
  <div id="content" class="text-center">
    <h1>{{title}}</h1>
    <app-game></app-game>
  </div>
</body>
