import { Component, ViewContainerRef, ComponentRef, ViewChild, QueryList, ComponentFactoryResolver, Injector, ComponentFactory, Input } from '@angular/core';
import { GameGridComponent } from '../game-grid/game-grid.component';

@Component({
  selector: 'app-game-status',
  templateUrl: './game-status.component.html',
  styleUrls: ['./game-status.component.css']
})
export class GameStatusComponent {
  title = 'Status Bar'
  @Input() mineCount: number = 3;
  @Input() rowCount: number = 5;
  @Input() columnCount: number = 5;

  @ViewChild('placeholder', { read: ViewContainerRef, static: true })
  public placeholder!: ViewContainerRef;
  constructor(private resolver: ComponentFactoryResolver, private injector: Injector) {}

  ngOnInit() {
    this.newGame();
  }

  //// Generate new minefield and clear game message
  newGame() {
    this.placeholder.detach();
    const componentFactory = this.resolver.resolveComponentFactory(GameGridComponent);
    const component = componentFactory.create(this.injector);
    this.placeholder.insert(component.hostView);

    component.instance.mines = this.mineCount;

    component.instance.columns = this.columnCount;
    component.instance.rows = this.rowCount;

    component.instance.gameOverEvent.subscribe((value) => this.gameOver(value));

    let element = document.getElementById('gameMessage');
    element!.innerHTML = '';
  }

  //// Create game message based on game result
  gameOver(win: boolean) {
    let element = document.getElementById('gameMessage');

    if (win) {      
      let newElement = '<h2>Winner<h2>';
      element!.innerHTML = newElement;
    }
    else {
      let newElement = '<h2>Go watch The Hurt Locker<h2>';
      element!.innerHTML = newElement;
    }
  }
}
<h3>{{title}}</h3>

<div>
  <p><label>Mines: {{mineCount}}</label></p>
  <p><label>Time Spent: </label></p>
  <button (click)="newGame()">Reset</button>
</div>

<div id="gameMessage"></div>

<ng-template #placeholder id="gameGrid"></ng-template>
