import { Component, Input, Output, EventEmitter, SimpleChanges } from '@angular/core';
import { SquareData } from '../square-data';

@Component({
  selector: 'app-grid-squares',
  templateUrl: './grid-squares.component.html',
  styleUrls: ['./grid-squares.component.css']
})
export class GridSquaresComponent {

  @Input() data!: SquareData;

  @Output() squareClickedEvent: EventEmitter<void> = new EventEmitter();

  value: string = "Mine?";

  ngOnInit() {
    this.displayValue();
  }

  ngOnChanges(changes: SimpleChanges) {
    this.displayValue();
  }

  displayValue() {
    if (this.data.state == 'hidden')
    {
      this.value = '?';
    }
    else if (this.data.mine)
    {
      this.value = 'Boom!';
    }
    else
    {
      this.value = this.data.adjacentMines == 0? `_`: `${this.data.adjacentMines}`;
    }
  }

  squareClicked() {
    this.squareClickedEvent.emit();
  }
}
<button type="button" [ngClass]="{'hidden': data.state === 'hidden', 'revealed': data.state === 'revealed', 'detonated': data.mine === true && data.state === 'revealed'}" (click)="squareClicked()">{{value}}</button>
