import { Component, ViewChild, ViewContainerRef, ComponentFactoryResolver, Injector } from '@angular/core';
import { GameStatusComponent } from '../game-status/game-status.component';

@Component({
  selector: 'app-options',
  templateUrl: './options.component.html',
  styleUrls: ['./options.component.css']
})
export class OptionsComponent {
  title = 'Game Options'
  mineCount: number = 3;
  rowCount: number = 5;
  columnCount: number = 5;

  @ViewChild('placeholder', { read: ViewContainerRef, static: true })
  public placeholder!: ViewContainerRef;
  constructor(private resolver: ComponentFactoryResolver, private injector: Injector) { }

  ngOnInit() {
    this.newGameInstance();
  }

  //// Generate new minefield and clear game message
  newGameInstance() {
    this.placeholder.detach();
    const componentFactory = this.resolver.resolveComponentFactory(GameStatusComponent);
    const component = componentFactory.create(this.injector);
    this.placeholder.insert(component.hostView);

    component.instance.mineCount = this.mineCount;
    component.instance.columnCount = this.columnCount; // breaks game logic if set from columnCount, unsure why
    component.instance.rowCount = this.rowCount;
  }

  mineInputChange(event: { target: { value: number; }; }) {
    if (event.target.value < 1) {
      event.target.value = 1;
    }

    this.mineCount = Number(event.target.value);
  }

  rowInputChange(event: { target: { value: number; }; }) {
    if (event.target.value < 1) {
      event.target.value = 1;
    }

    this.rowCount = Number(event.target.value);
  }

  columnInputChange(event: { target: { value: number; }; }) {
    if (event.target.value < 1) {
      event.target.value = 1;
    }

    this.columnCount = Number(event.target.value);
  }
}
<h2>{{title}}</h2>

<div id="gameOptionsForm">

  <div class="form-floating mb-3 mt-3">
    <input type="text" class="form-control" id="email" placeholder="Enter email" name="email">
    <label for="email">Email</label>
  </div>


  <div class="container">
    <div class="row">
      <div class="col-7 col-md">
        <div class="input-group">
          <span class="input-group-text">Columns</span>
          <input #columnAmount class="form-control" id="columnCount" type="number" required value="{{columnCount}}" min="1" (change)="columnInputChange($any($event))" />
        </div>
      </div>
      <div class="col-7 col-md">
        <div class="input-group">
          <span class="input-group-text">Rows</span>
          <input #rowAmount class="form-control" id="rowCount" type="number" required value="{{rowCount}}" min="1" (change)="rowInputChange($any($event))" />
        </div>
      </div>
      <div class="col-7 col-md">
        <div class="input-group">
          <span class="input-group-text">Mines</span>
          <input #mineAmount class="form-control" id="mineCount" type="number" required value="{{mineCount}}" min="1" (change)="mineInputChange($any($event))" />
        </div>
      </div>
    </div>
    <div class="row">
      <div class="col-4">
        <button class="btn btn-primary" style="width:100%" (click)="newGameInstance()">Apply</button>
      </div>
    </div>
  </div>

</div>

<ng-template #placeholder id="gameGrid"></ng-template>
