import { Component } from '@angular/core';

@Component({
  selector: 'app-grid-squares',
  templateUrl: './grid-squares.component.html',
  styleUrls: ['./grid-squares.component.css']
})
export class GridSquaresComponent {
  Bomb: boolean = false;
  AdjacentBombs: number = 0;
  Coordinates: [number, number] = [0, 0];
  Value: string = "Bomb?";

  chooseSquare() {
    if (this.Bomb) {
      this.Value = "Bomb!";
    }
    else {
      this.Value = `${this.AdjacentBombs}`;
    }
  }
}
<button (click)="chooseSquare()">{{Value}}</button>
