import { Component, ViewContainerRef, ComponentRef, ViewChild, QueryList } from '@angular/core';
import { GameGridComponent } from '../game-grid/game-grid.component';

@Component({
  selector: 'app-game-status',
  templateUrl: './game-status.component.html',
  styleUrls: ['./game-status.component.css']
})
export class GameStatusComponent {
  title = 'Status Bar'
  mineSelection: number = 1;
  renderGrid: boolean = true;

  //@ViewChild("viewContainerRef", { read: ViewContainerRef }) vcr!: ViewContainerRef;
  //ref!: ComponentRef<GameGridComponent>

  ngAfterViewInit() {
    //this.addChild();
  }

  //addChild() {
  //  this.ref = this.vcr.createComponent(GameGridComponent)
  //}

  //removeChild() {
  //  const index = this.ref != undefined ? this.vcr.indexOf(this.ref.hostView) : -1;
  //  if (index != -1) this.vcr.remove(index);
  //}

  resetGame() {
    //this.removeChild();
    //this.addChild();
    this.renderGrid = false;
  }

  gameOver(win: boolean) {
    let element = document.getElementById('gameGrid');

    if (win) {      
      let newElement = '<h2>Winner<h2>';
      element!.insertAdjacentHTML("beforebegin", newElement);
    }
    else {
      let newElement = '<h2>Go watch The Hurt Locker<h2>';
      element!.insertAdjacentHTML("beforebegin", newElement);
    }
  }

}
