import { Component, ViewChild, ElementRef, ComponentRef, AfterViewInit, Input, Output, EventEmitter } from '@angular/core';
import { GridSquaresComponent } from '../grid-squares/grid-squares.component';
import { SquareData } from '../square-data';

@Component({
  selector: 'app-game-grid',
  templateUrl: './game-grid.component.html',
  styleUrls: ['./game-grid.component.css']
})
export class GameGridComponent {
  title = 'Minefield'

  @Input() mines: number = 7;

  @Output() gameOverEvent: EventEmitter<boolean> = new EventEmitter();

  colArray: number[] = [];
  rowArray: number[] = [];
  squares: SquareData[] = [];
  columns = 5;
  rows = 10;
  revealedEmpty = 0;

  ngOnInit() {
    this.setDimension();

    //var temp = document.getElementById('minefield');
    //temp!.style.gridTemplateColumns = `repeat(${this.columns}, 50px);`
  }

  //// Generate grid and randomise bomb locations
  setDimension() {
    this.squares = [];
    let totalSquares = this.rows * this.columns;

    for (let i = 0; i < totalSquares; i++) {

      if (i < this.mines) {
        const squareData: SquareData = {
          mine: true,
          state: 'hidden'
        };

        this.squares.push(squareData);
      }
      else {
        const squareData: SquareData = {
          mine: false,
          state: 'hidden'
        };
        this.squares.push(squareData);
      }
    }

    this.squares.sort(() => Math.random() - 0.5);

    let index = 0;

    this.squares.forEach(() => {
      let adjacentSquares: number[] = [];
      if ((index % this.columns) - 1 >= 0) { adjacentSquares.push(index - 1) }; // left
      if ((index % this.columns) + 1 < this.columns) { adjacentSquares.push(index + 1) }; // right
      if (index - this.columns >= 0) { adjacentSquares.push(index - this.columns) }; // top
      if (index + this.columns < totalSquares) { adjacentSquares.push(index + this.columns) }; // bottom
      if ((index - this.columns) - 1 >= 0 && (index % this.columns) - 1 >= 0) { adjacentSquares.push(index - this.columns - 1) }; // top left
      if ((index - this.columns) + 1 >= 0 && (index % this.columns) + 1 < this.columns) { adjacentSquares.push(index - this.columns + 1) }; // top right
      if ((index + this.columns) - 1 < totalSquares && (index % this.columns) - 1 >= 0) { adjacentSquares.push(index + this.columns - 1) }; // bottom left
      if ((index + this.columns) + 1 < totalSquares && (index % this.columns) + 1 < this.columns) { adjacentSquares.push(index + this.columns + 1) }; // bottom right
      this.squares[index].adjacent = adjacentSquares;
      index++;
    })
  }

  //// Event emitted from square, check for adjacent mines, if none repeat for adjacent squares
  squareClicked(index: number) {
    let square = this.squares[index];
    let adjacentMines = 0;

    square.adjacent?.forEach((index: number) => {
      if (this.checkForMine(index)) {        
        adjacentMines++;
      }
    })

    square = { state: 'revealed', mine: square.mine, adjacentMines: adjacentMines, adjacent: square.adjacent };
    this.squares[index] = square;

    if (adjacentMines == 0) {
      square.adjacent ?.forEach((check: number) => {
        if (this.squares[check].state == 'hidden') {
          this.squareClicked(check);
        }
      })
    }

    var temp = document.getElementById('minefield');
    temp!.style.gridTemplateColumns = `repeat(${this.columns}, 50px);`

    this.checkForWin(square);
  }

  //// Checks if a square is a mine
  checkForMine(index: number) {
    let square = this.squares[index];

    if (square.mine) {
      return true;
    }
    else {
      return false;
    }
  }

  checkForWin(currentSquare: SquareData) {
    if (currentSquare.mine) {
      this.gameOverEvent.emit(false);
    }
    else {
      this.revealedEmpty++;

      if (this.revealedEmpty == this.columns * this.rows - this.mines) {
        this.gameOverEvent.emit(true);
      }
    }
  }
}
<h3>{{title}}</h3>

<div>
  <!--<input type="number" [(ngModel)]="rows" />
  <input type="number" [(ngModel)]="columns" />-->
  <!--<button (click)="setDimension()">Generate grid</button>-->

  <div id="minefield" class="grid">
    <app-grid-squares *ngFor="let s of squares; let idx=index" [data]="s" (squareClickedEvent)="squareClicked(idx)"></app-grid-squares>
  </div>

</div>
