@Component({
  selector: 'app-game-status',
  templateUrl: './game-status.component.html',
  styleUrls: ['./game-status.component.css']
})
export class GameStatusComponent {
  title = 'Status Bar'
}
<p>{{title}}</p>

<label>Total bombs:</label>
<label>Time Spent:</label>
