import { Component, ViewChild, ViewContainerRef, ComponentFactoryResolver, Injector, Input, EventEmitter, Output } from '@angular/core';
import { GameStatusComponent } from '../game-status/game-status.component';
import { GameOptionsData } from '../game-options-data';

@Component({
  selector: 'app-options',
  templateUrl: './options.component.html',
  styleUrls: ['./options.component.css']
})
export class OptionsComponent {
  title = 'Game Options'
  @Input() mineCount: number = 3;
  @Input() rowCount: number = 5;
  @Input() columnCount: number = 5;
  @Output() optionsChangedEvent: EventEmitter<GameOptionsData> = new EventEmitter();

  mineInputChange(event: { target: { value: number; }; }) {
    if (event.target.value < 1) {
      event.target.value = 1;
    }

    this.mineCount = Number(event.target.value);
  }

  rowInputChange(event: { target: { value: number; }; }) {
    if (event.target.value < 1) {
      event.target.value = 1;
    }

    this.rowCount = Number(event.target.value);
  }

  columnInputChange(event: { target: { value: number; }; }) {
    if (event.target.value < 1) {
      event.target.value = 1;
    }

    this.columnCount = Number(event.target.value);
  }

  optionsChanged() {

    const data: GameOptionsData = {
      mineCount: this.mineCount,
      columnCount: this.columnCount,
      rowCount: this.rowCount
    }

    this.optionsChangedEvent.emit(data);
  }
}
<h2>{{title}}</h2>

<div id="gameOptionsForm">

  <div class="container">
    <div class="row">
      <div class="col-7 col-md">
        <div class="input-group">
          <span class="input-group-text">Columns</span>
          <input #columnAmount class="form-control" id="columnCount" type="number" required value="{{columnCount}}" min="1" (change)="columnInputChange($any($event))" />
        </div>
      </div>
      <div class="col-7 col-md">
        <div class="input-group">
          <span class="input-group-text">Rows</span>
          <input #rowAmount class="form-control" id="rowCount" type="number" required value="{{rowCount}}" min="1" (change)="rowInputChange($any($event))" />
        </div>
      </div>
      <div class="col-7 col-md">
        <div class="input-group w-100">
          <span class="input-group-text">Mines</span>
          <input #mineAmount class="form-control" id="mineCount" type="number" required value="{{mineCount}}" min="1" (change)="mineInputChange($any($event))" />
        </div>
      </div>
    </div>
    <div class="row">
      <div class="col-4">
        <button class="btn btn-primary" style="width:100%" (click)="optionsChanged()">Apply</button>
      </div>
    </div>
  </div>

</div>

<ng-template #placeholder id="gameGrid"></ng-template>
