import { Component } from '@angular/core';

@Component({
  selector: 'app-options',
  templateUrl: './options.component.html',
  styleUrls: ['./options.component.css']
})
export class OptionsComponent {
  title = 'Game Options'
  mineCount: number = 3;
  rowCount: number = 5;
  columnCount: number = 5;

  mineInputChange(event: { target: { value: number; }; }) {
    this.mineCount = event.target.value;
  }

  rowInputChange(event: { target: { value: number; }; }) {
    this.rowCount = event.target.value;
  }

  columnInputChange(event: { target: { value: number; }; }) {
    this.columnCount = event.target.value;
  }
}
<h2>{{title}}</h2>

<label for="mineCount">Mines: </label>
<input #mineAmount id="mineCount" type="number" required value="{{mineCount}}" min="0" (change)="mineInputChange($any($event))" />

<label for="columnCount">Columns: </label>
<input #mineAmount id="columnCount" type="number" required value="{{columnCount}}" min="0" (change)="columnInputChange($any($event))" />

<label for="rowCount">Rows: </label>
<input #minAomount id="rowCount" type="number" required value="{{rowCount}}" min="0" (change)="rowInputChange($any($event))" />

<app-game-status></app-game-status>
