import { Component } from '@angular/core';

@Component({
  selector: 'app-game',
  templateUrl: './game.component.html',
  styleUrls: ['./game.component.css']
})
export class GameComponent {
  title = 'Game'
}
<div class="gameBar">
  <app-options (optionsChangedEvent)="setOptions($event)"></app-options>
</div>

<div class="gameBar">
  <ng-template #placeholder id="gameGrid"></ng-template>
</div>
