import { Component, ViewContainerRef, ComponentRef, ViewChild } from '@angular/core';
import { GameGridComponent } from '../game-grid/game-grid.component';

@Component({
  selector: 'app-game-status',
  templateUrl: './game-status.component.html',
  styleUrls: ['./game-status.component.css']
})
export class GameStatusComponent {
  title = 'Status Bar'

  @ViewChild("viewContainerRef", { read: ViewContainerRef }) vcr!: ViewContainerRef;
  ref!: ComponentRef<GameGridComponent>

  ngAfterViewChecked() {
    this.addChild();
  }

  addChild() {
    this.ref = this.vcr.createComponent(GameGridComponent)
  }

  removeChild() {
    const index = this.ref != undefined ? this.vcr.indexOf(this.ref.hostView) : -1;
    if (index != -1) this.vcr.remove(index);
  }


  resetGame() {
    let mines = 6;

    this.removeChild();
    this.addChild();
  }
}
<h3>{{title}}</h3>

<label for="mineAmount">Total mines: </label>
<input id="mineAmount" type="number" />
<label>Time Spent: </label>
<button>Give up</button>
<button (click)="resetGame()">Reset</button>

<!--<div id="gameGrid">
  <app-game-grid [mines]="10"></app-game-grid>
</div>-->

<ng-template #viewContainerRef></ng-template>
